import React, {useState, useEffect} from 'react'
import axios from 'axios'
import Publish from '../components/Publish/Publish'
import {Button} from '../components/Button/Button'
import Link from 'react-router-dom'

function Treatments() {

    const[treatments, settreatments] = useState([])
    const [loading, setloading] = useState()
    const [error, seterror] = useState()

    useEffect(async() => {
        try {
          setloading(true)
            const data  = (await axios.get('/api/treatments/getalltreatments')).data

            settreatments(data)
            setloading(false)

        } catch (error) {
            seterror(true)
            console.log(error)
            setloading(false)
        }
    },[])

  return (
    <div className='container'>
      <div className='row justify-content-center mt-5'>
      {loading ? (
      <h1>loading...</h1>) : error ? (<h1>Error</h1>) : (treatments.map(treatments=>{
        return <div className='com-md-9 mt-2'>
          <Publish treatments={treatments} />
        </div>
      }))}
      </div>
      <Link to={`/book/${treatments._id}`}>
          <span className='m-2'>
            <Button>
                Book Now
            </Button>
          </span>
          </Link>
    </div>
  )
}
 export default Treatments
